mapper.select:
SELECT id, userId, password, nickname, isWithdraw, isAdmin, createTime
FROM user
WHERE userId = #{userId}

mapper.update:
UPDATE user
SET password=#{password},
    nickname=#{nickname},
    isWithdraw=#{isWithdraw},
    status=#{status}
WHERE id = #{id}

